-- Migration to RE-populate test questions for DISC, EQ, Soft Skills, and Motivation
-- and ADD missing questions for MBTI.

-- Helper function to get test_id by code
CREATE OR REPLACE FUNCTION get_test_id(test_code text) RETURNS uuid AS $$
  SELECT id FROM tests WHERE code = test_code;
$$ LANGUAGE sql;

-- 1. CLEANUP: Delete existing questions for tests we are fully repopulating
-- We KEEP MBTI questions 1-40 as they are valid.
DELETE FROM test_questions 
WHERE test_id IN (
  get_test_id('disc'),
  get_test_id('eq'),
  get_test_id('soft_skills'),
  get_test_id('motivation')
);

