INSERT INTO test_questions (id, test_id, question_number, text_ru, text_kk, text_en, scale_code, reverse_scored, options) VALUES
(gen_random_uuid(), get_test_id('mbti'), 41, 'Вам нравится быть в центре внимания на вечеринках?', 'Кештерде назар орталығында болғанды ұнатасыз ба?', 'Do you enjoy being the center of attention at parties?', 'EI', false, '{"ru": ["Да", "Нет"], "kk": ["Иә", "Жоқ"], "en": ["Yes", "No"], "values": [1, 0]}'::jsonb),
(gen_random_uuid(), get_test_id('mbti'), 42, 'Вы предпочитаете сначала действовать, а потом думать?', 'Сіз алдымен әрекет етуді, содан кейін ойлануды қалайсыз ба?', 'Do you prefer to act first and think later?', 'EI', false, '{"ru": ["Да", "Нет"], "kk": ["Иә", "Жоқ"], "en": ["Yes", "No"], "values": [1, 0]}'::jsonb),
(gen_random_uuid(), get_test_id('mbti'), 43, 'Вам легко заговорить с незнакомым человеком?', 'Бейтаныс адаммен сөйлесу сізге оңай ма?', 'Is it easy for you to start a conversation with a stranger?', 'EI', false, '{"ru": ["Да", "Нет"], "kk": ["Иә", "Жоқ"], "en": ["Yes", "No"], "values": [1, 0]}'::jsonb),
(gen_random_uuid(), get_test_id('mbti'), 44, 'Вы считаете себя спокойным и сдержанным человеком?', 'Сіз өзіңізді сабырлы және ұстамды адаммын деп санайсыз ба?', 'Do you consider yourself a calm and reserved person?', 'EI', true, '{"ru": ["Да", "Нет"], "kk": ["Иә", "Жоқ"], "en": ["Yes", "No"], "values": [0, 1]}'::jsonb),
(gen_random_uuid(), get_test_id('mbti'), 45, 'Вы быстро устаете от большого количества общения?', 'Сіз көп қарым-қатынастан тез шаршайсыз ба?', 'Do you get tired quickly from a lot of socializing?', 'EI', true, '{"ru": ["Да", "Нет"], "kk": ["Иә", "Жоқ"], "en": ["Yes", "No"], "values": [0, 1]}'::jsonb);
INSERT INTO test_questions (id, test_id, question_number, text_ru, text_kk, text_en, scale_code, reverse_scored, options) VALUES
(gen_random_uuid(), get_test_id('mbti'), 46, 'Вы больше доверяете своему опыту, чем теоретическим возможностям?', 'Сіз теориялық мүмкіндіктерге қарағанда өз тәжірибеңізге көбірек сенесіз бе?', 'Do you trust your experience more than theoretical possibilities?', 'SN', false, '{"ru": ["Да", "Нет"], "kk": ["Иә", "Жоқ"], "en": ["Yes", "No"], "values": [1, 0]}'::jsonb),
(gen_random_uuid(), get_test_id('mbti'), 47, 'Вам нравится работать с конкретными фактами и деталями?', 'Сізге нақты фактілермен және егжей-тегжейлермен жұмыс істеген ұнай ма?', 'Do you like working with concrete facts and details?', 'SN', false, '{"ru": ["Да", "Нет"], "kk": ["Иә", "Жоқ"], "en": ["Yes", "No"], "values": [1, 0]}'::jsonb),
(gen_random_uuid(), get_test_id('mbti'), 48, 'Вы часто думаете о том, как улучшить будущее?', 'Сіз болашақты қалай жақсартуға болатыны туралы жиі ойлайсыз ба?', 'Do you often think about how to improve the future?', 'SN', true, '{"ru": ["Да", "Нет"], "kk": ["Иә", "Жоқ"], "en": ["Yes", "No"], "values": [0, 1]}'::jsonb),
(gen_random_uuid(), get_test_id('mbti'), 49, 'Вас больше интересуют идеи и концепции, чем их практическое применение?', 'Сізді практикалық қолданудан гөрі идеялар мен тұжырымдамалар қызықтырады ма?', 'Are you more interested in ideas and concepts than their practical application?', 'SN', true, '{"ru": ["Да", "Нет"], "kk": ["Иә", "Жоқ"], "en": ["Yes", "No"], "values": [0, 1]}'::jsonb),
(gen_random_uuid(), get_test_id('mbti'), 50, 'Вы предпочитаете проверенные методы новым и непроверенным?', 'Сіз жаңа және тексерілмеген әдістерден гөрі тексерілген әдістерді қалайсыз ба?', 'Do you prefer proven methods over new and untested ones?', 'SN', false, '{"ru": ["Да", "Нет"], "kk": ["Иә", "Жоқ"], "en": ["Yes", "No"], "values": [1, 0]}'::jsonb);
